<script>
/**
 * checking pagination buttons dislabling and remove buttons values to add images
 */
function pagination_buttons(){
	//getting 4 buttons
	var btnFirst = document.getElementById("btnFirst");
	var btnPrev  = document.getElementById("btnPrev");
	var btnNext  = document.getElementById("btnNext");
	var btnLast  = document.getElementById("btnLast");

	//removing values
	btnFirst.removeAttribute("value");
	btnPrev.removeAttribute("value");
	btnNext.removeAttribute("value");
	btnLast.removeAttribute("value");

	//checking disable and adding classes
	if(btnFirst.disabled == true){ btnFirst.className = "btnFirst_dis"; } else{ btnFirst.className = "btnFirst"; }
	if(btnPrev.disabled == true){ btnPrev.className = "btnPrev_dis"; } else{ btnPrev.className = "btnPrev"; }
	if(btnNext.disabled == true){ btnNext.className = "btnNext_dis"; } else{ btnNext.className = "btnNext"; }
	if(btnLast.disabled == true){ btnLast.className = "btnLast_dis"; } else{ btnLast.className = "btnLast"; }
}
</script>
